@SuppressWarnings("restriction") public class TargetElementProviders implements IModelElementProvider {  private ResourceSet resourceSet;  @Override  public void getModelElements(Filter filter, ModelResultHandler handler) {  if (resourceSet == null) {  resourceSet = Util.getModelElementResources(); 		}  applyFilter(filter, handler); 	}  private void applyFilter(Filter filter, ModelResultHandler handler) {  for (final Resource res : resourceSet.getResources()) {  final TreeIterator<EObject> it = EcoreUtil.getAllContents(res,  true); } } } 
