  // (TableViewer) gridTableViewer, // new CustomFocusCellHighlighter(gridTableViewer);  final ColumnViewerEditorActivationStrategy actSupport = new ColumnViewerEditorActivationStrategy(  gridTableViewer) {  @SuppressWarnings("unchecked")  @Override  protected boolean isEditorActivationEvent(ColumnViewerEditorActivationEvent event) {  if (event.eventType == ColumnViewerEditorActivationEvent.TRAVERSAL 						|| event.eventType == ColumnViewerEditorActivationEvent.MOUSE_DOUBLE_CLICK_SELECTION 						|| event.eventType == ColumnViewerEditorActivationEvent.PROGRAMMATIC) {  return true; 					}  if (event.eventType == ColumnViewerEditorActivationEvent.MOUSE_CLICK_SELECTION 						&& gridTableViewer.isCellEditorActive()) {  gridTableViewer.applyEditorValue(); 					}  if (event.eventType == ColumnViewerEditorActivationEvent.KEY_PRESSED) {  for (final int keyCode : Arrays.asList(SWT.CTRL, SWT.ALT, SWT.SHIFT)) {  if ((event.keyCode & keyCode) != 0 || (event.stateMask & keyCode) != 0) {  return false; 							} 						}  return !Arrays 							.asList(SWT.ARROW_UP, SWT.ARROW_DOWN, SWT.ARROW_LEFT, SWT.ARROW_RIGHT, SWT.TAB, SWT.CR) 							.contains(event.keyCode); 					}  return false; 				} 			};  actSupport.setEnableEditorActivationWithKeyboard(true);  GridViewerEditor.create(  gridTableViewer, }
