 private CDOLockChangeInfo makeUnlockChangeInfo(CommitTransactionResult result){       } List<CDOLockState> objectsToUnlock = new ArrayList<>();  for (Map.Entry<CDOObject, CDOLockState> entry : lockStates.entrySet())       {  CDOObject object = entry.getKey();  if (options().isEffectiveAutoReleaseLock(object))         {  InternalCDOLockState lockState = (InternalCDOLockState)entry.getValue();  lockState.updateFrom(InternalCDOLockState.UNLOCKED);  objectsToUnlock.add(lockState);         }       } 
