 public EObject getFirstSelectedEObject(ISelection selection) {  Object selected = getFirstSelectedElement(selection);  if (selected instanceof EObject) {  return (EObject) selected; 		}  return null; 	}  public EObject getEObjectFromMouseEvent(MouseEvent event) {  Object source = event.getSource();  if (source instanceof Tree) {  Tree tree = (Tree) source;  if (tree.getSelectionCount() == 1) {  Object selection = tree.getSelection()[0].getData();  if (selection instanceof EObject) {  return (EObject) selection; 				} 			} 		}  return null; 	} 
