public class JavaClassHelpResolver implements IHelpResolver {  private final String fPackageName;  private final String fClassName;  private Class<?> fClazz;  public JavaClassHelpResolver(final String packageName, final String className) {  fPackageName = packageName.replace('.', '/');  fClassName = className;  try {  fClazz = JavaClassHelpResolver.class.getClassLoader().loadClass(packageName + "." + className.replace('.', '$')); 		} catch (final ClassNotFoundException e) {  fClazz = null; 		} 	}  @Override  public String resolveHelp() {  final IProject project = new VirtualProject();  final IJavaProject javaProject = JavaCore.create(project);  final ITypeRoot typeRoot = resolveTypeRoot(javaProject);  final IJavaElement javaElement = resolveJavaElement(typeRoot);  final JavadocBrowserInformationControlInput hoverInfo = JavadocHover.getHoverInfo(new IJavaElement[] { javaElement }, typeRoot, new Region(0, 1), null); } } 
