 @Override  public void modify(Object element, String property, Object value) {  TableItem tableItem = (TableItem) element;  Object data = tableItem.getData();  Command cmd = null;  switch (property) {  case ARRAY_SIZE:  cmd = new ChangeArraySizeCommand((VarDeclaration) data, (String) value);  break;  case INITIAL_VALUE:  cmd = new ChangeInitialValueCommand((VarDeclaration) data, (String) value);  break;  default:  super.modify(element, property, value); } } 
