  // we don't want to optimize if we've extended the filter ... this // needs to be addressed in 3.4  // https://bugs.eclipse.org/bugs/show_bug.cgi?id=186404  if (matcher == null && useEarlyReturnIfMatcherIsNull) {  return elements;         }  if (!useCache) {  return super.filter(viewer, parent, elements);         }  Object[] filtered = cache.get(parent);  if (filtered == null) {  Boolean foundAny = foundAnyCache.get(parent);  if (foundAny != null && !foundAny.booleanValue()) {  filtered = EMPTY;             } else {  filtered = super.filter(viewer, parent, elements);             }  cache.put(parent, filtered);         }  return filtered;     }
