 @Override public void setBottomTrim(WLayoutedWidget<MTrimBar> trimBar) {  if (trimBar == null) {  if( this.rootPane instanceof TrimmedWindow ) { 					((TrimmedWindow)this.rootPane).setBottomTrim(null); 				} else {  this.trimPane.setBottom(null);  				}   			} else {  if( this.rootPane instanceof TrimmedWindow ) { 					((TrimmedWindow)this.rootPane).setBottomTrim((Node) trimBar.getStaticLayoutNode()); 				} else {  this.trimPane.setBottom((Node) trimBar.getStaticLayoutNode());  				} 			} } 
