 protected Control createSWTControl(Composite parent) throws DatabindingFailedExc{  final Button bUnset = new Button(dateTimeComposite, SWT.PUSH);  GridDataFactory.fillDefaults().grab(false, false).align(SWT.CENTER, SWT.CENTER).applyTo(bUnset);  bUnset.setImage(imageRegistryService.getImage(FrameworkUtil.getBundle(getClass()), "icons/delete.png"));   bUnset.setData(CUSTOM_VARIANT, "org_eclipse_emf_ecp_control_dateTime_buttonUnset");   bUnset.setToolTipText(getLocalizedString(MessageKeys.DateTimeControlSWTRenderer_CleanDate)); } 
