 private List<EStructuralFeature> getUnreferencedSegmentAttributes(EClass eClass){  if (domainModelReference == null) { continue; 				}  final Iterator<EStructuralFeature> structuralFeatureIterator = domainModelReference 					.getEStructuralFeatureIterator();  while (structuralFeatureIterator.hasNext()) {  final EStructuralFeature feature = structuralFeatureIterator.next();  if (feature != null && feature.getEContainingClass() != null 						&& feature.getEContainingClass().equals(eClass)) {  result.add(feature); 					} 				} 			} 
