 /** 	 * {@inheritDoc} 	 *  	 * @since 1.5 	 */  @Override  public void addNewModelElements(EObject eObject, EReference eReference) {  if (eReference == null) {  return; 		}  final Collection<EClass> classes = ECPUtil.getSubClasses(eReference.getEReferenceType());  final SelectionComposite<TreeViewer> helper = ECPCompositeFactory.getSelectModelClassComposite(  new HashSet<EPackage>(),  new HashSet<EPackage>(), classes);  final EObject newModelElement = SelectModelElementWizardFactory.openCreateNewModelElementDialog(helper);  if (newModelElement == null) {  return; } } 
