 btnRemove.addSelectionListener(new SelectionAdapter() { @Override  public void widgetSelected(SelectionEvent event) {  Recommendation<ISnippet> recommendation = cast(selection.getValue());  try {  for (ISnippetRepository repo : repos) {  repo.delete(recommendation.getProposal().getUuid());                     }                 } catch (Exception e) {  Throwables.propagate(e);                 }             }         }); 
