 // Cover children Button coverChildrenButton = new Button(result, SWT.CHECK);  coverChildrenButton.setText(Messages.IgnoreChoicesDialog_IncludeChildren);  coverChildrenButton.setSelection(getData().getCoverChildren());  coverChildrenButton.addSelectionListener(new SelectionAdapter() {  /**        * @see org.eclipse.swt.events.SelectionAdapter#widgetSelected(org.eclipse.swt.events.SelectionEvent)        */  @Override  public void widgetSelected(SelectionEvent e_p) {  getData().setCoverChildren(!getData().getCoverChildren());       }     });  return result; 
