 public void notifyChanged ( Notification notification ){  switch ( notification.getFeatureID ( MappedSourceValue.class ) )         {  case ComponentPackage.MAPPED_SOURCE_VALUE__SHORT_DESCRIPTION:  case ComponentPackage.MAPPED_SOURCE_VALUE__NAME:  case ComponentPackage.MAPPED_SOURCE_VALUE__CUSTOMIZATION_TAGS:  fireNotifyChanged ( new ViewerNotification ( notification, notification.getNotifier (), false, true ) );  return;  case ComponentPackage.MAPPED_SOURCE_VALUE__PROPERTIES:  case ComponentPackage.MAPPED_SOURCE_VALUE__INPUT:  fireNotifyChanged ( new ViewerNotification ( notification, notification.getNotifier (), true, false ) );  return; } } 
