 protected void respondingNewPermissions(CDODataOutput out) throws Exception{  IPermissionManager permissionManager = session.getManager().getPermissionManager(); if (permissionManager != null)     {  InternalCDORevision[] newObjects = commitContext.getNewObjects();  InternalCDORevision[] dirtyObjects = commitContext.getDirtyObjects();  out.writeInt(newObjects.length + dirtyObjects.length);  respondingNewPermissions(out, permissionManager, session, newObjects);  respondingNewPermissions(out, permissionManager, session, dirtyObjects);  }  else  {  out.writeBoolean(false);     }   } 
