 @Override  public Object[] create(ICreateContext context) {  T element = null;  try {  ModelHandler handler = ModelHandler.getInstance(getDiagram());  element = createBusinessObject(context);  if (FeatureSupport.isTargetLane(context) && element instanceof FlowNode) { 				((FlowNode) element).getLanes().add( 						(Lane) getBusinessObjectForPictogramElement(context.getTargetContainer())); 			}  handler.addFlowElement(getBusinessObjectForPictogramElement(context.getTargetContainer()), element);  } catch (IOException e) {  Activator.logError(e); 		}  PictogramElement pe = null;  pe = addGraphicalRepresentation(context, element);  return new Object[] { element, pe }; 	}  protected abstract String getStencilImageId(); 
