 serv.activateHandler(org.eclipse.ui.IWorkbenchCommandConstants.EDIT_COPY, new AbstractHandler() { @Override  public Object execute(ExecutionEvent event) throws ExecutionException {  int index = table.getSelectionIndex();  if (index < 0) {  return Status.CANCEL_STATUS; 				}  Object entry = parent.getEntry(index);  cb.setContents(entry);  return Status.OK_STATUS; 			} 		});  serv.activateHandler(org.eclipse.ui.IWorkbenchCommandConstants.EDIT_PASTE, new AbstractHandler() {  @Override  public Object execute(ExecutionEvent event) throws ExecutionException {  Object entry = Clipboard.getDefault().getContents();  int index = table.getSelectionIndex();  if (index < 0) {  index = table.getItemCount(); 				}  parent.addEntry(entry, index);  return Status.OK_STATUS; 			} 		});  serv.activateHandler(org.eclipse.ui.IWorkbenchCommandConstants.EDIT_CUT, new AbstractHandler() {  @Override  public Object execute(ExecutionEvent event) throws ExecutionException {  int index = table.getSelectionIndex();  if (index < 0) {  return Status.CANCEL_STATUS; 				}  Object entry = parent.removeEntry(index);  cb.setContents(entry);  return Status.OK_STATUS; 			} 		}); 
