  var texttitle = eclipseEvents[i].title + ", " + eclipseEvents[i].dateTime.toDateString();  if (eclipseEvents[i].endDateTime != undefined) {   texttitle += " - " + eclipseEvents[i].endDateTime.toDateString();  }  var description = createHtmlDescription(eclipseEvents[i]);  // create marker for map:  var marker = createMarker(eclipseEvents[i], texttitle, description);  // collect marker depending on eventtype  if (eventtype in markers4Layer == false) {  markers4Layer[eventtype] = [];  }  markers4Layer[eventtype].push(marker);  fillDescriptionsArea(eventtype, texttitle, description, i);  }
