 public void menuAboutToShow(IMenuManager menuManager){  super.menuAboutToShow(menuManager); MenuManager submenuManager = null;  submenuManager = new MenuManager(PluginDelegator.INSTANCE.getString("_UI_CreateChild_menu_item"));  populateManager(submenuManager, createChildActions, null);  menuManager.insertBefore("edit", submenuManager);  submenuManager = new MenuManager(PluginDelegator.INSTANCE.getString("_UI_CreateSibling_menu_item"));  populateManager(submenuManager, createSiblingActions, null);  menuManager.insertBefore("edit", submenuManager);   } 
