 public void run(IProgressMonitor monitor) throws InvocationTargetException{  StatusAdapter status; if (cause.getStatus().getCode() == IResourceStatus.CASE_VARIANT_EXISTS)         {  status = new StatusAdapter(StatusUtil.newStatus(IStatus.WARNING, NLS.bind(  ResourceMessages.NewProject_caseVariantExistsError, newProjectHandle.getName()), cause));         }  else         {  status = new StatusAdapter(StatusUtil.newStatus(cause.getStatus().getSeverity(),  ResourceMessages.NewProject_errorMessage, cause));         }  status.setProperty(IStatusAdapterConstants.TITLE_PROPERTY, ResourceMessages.NewProject_errorMessage);  StatusManager.getManager().handle(status, StatusManager.BLOCK);       }  else       {  StatusAdapter status = new StatusAdapter(new Status(IStatus.WARNING, IDEWorkbenchPlugin.IDE_WORKBENCH, 0, NLS             .bind(ResourceMessages.NewProject_internalError, t.getMessage()), t));  status.setProperty(IStatusAdapterConstants.TITLE_PROPERTY, ResourceMessages.NewProject_errorMessage);  StatusManager.getManager().handle(status, StatusManager.LOG | StatusManager.BLOCK);       } 
