 public String isValid(String newText){     { if (StringUtil.isEmpty(newText))       {  return "Branch name cannot be empty";       }  if (newText.contains(CDOBranch.PATH_SEPARATOR))       {  return "Branch name cannot contain path separators";       }  BranchNode branchNode = UIUtil.getElement(getSelection(), BranchNode.class);  CDOBranch branch = branchNode.getBranch();  if (branch.getBranch(newText) != null)       {  return "A branch with that name already exists";       }  return null; } } 
