 public static IAnnotationAttributeInitializer getAnnotationAttributeInitializerForName(Name name) {  if (name != null) {  if (name.isSimpleName()) {  return getSimpleNameToDefinitionMap().get(((SimpleName)name).getIdentifier())                     .getAnnotationAttributeInitializer();             } else if (name.isQualifiedName()) {  return getQualifiedNameToDefinitionMap().get(name.getFullyQualifiedName())                     .getAnnotationAttributeInitializer();             }         }  return null; } 
