 public void startTable( ITableContent table ){ 	{  ContainerSizeInfo sizeInfo = engine.getCurrentContainer( ).getSizeInfo( );  int width = sizeInfo.getWidth( );  ColumnsInfo info = LayoutUtil.createTable( table, width );  if( info == null ) {  return;	 		}  String caption = table.getCaption( );  if(caption != null)  		{			  engine.addCaption( caption ); 		}  engine.addTable( table, info, sizeInfo ); 	} } 
