 public String read(ExtendedDataInput in) throws IOException{  return null;    }  if (id == STRING_FOLLOWS)     {  id = readInt(in);  String string = readString(in);  synchronized (stringToID)       {  stringToID.put(string, id);  idToString.put(id, string);       }  return string;     }  else     {  synchronized (stringToID)       {  String string = idToString.get(id);  if (string == null)         {  throw new IOException("String ID unknown: " + id);         }  return string;       }     } 
