 public void commit(IProgressMonitor progressMonitor) throws TransactionException{  for (InternalCDOTransaction transaction : transactions)     {  CDOCommitContext context = transaction.createCommitContext();  CDOXATransactionCommitContext xaContext = new CDOXATransactionCommitContext(this, context);  xaContext.setState(CDOXAPhase1State.INSTANCE);  activeContext.put(transaction, xaContext); } } 
