 public void testKeepValue() throws Exception{  assertEquals("Test", c1.getName());  }  private void detachResource(ResourceSet rset, CDOResource resource) throws Exception   {  Order order = getModel1Factory().createOrder();  OrderDetail orderDetail = getModel1Factory().createOrderDetail();  resource.getContents().add(order);  order.getOrderDetails().add(orderDetail);  assertActive(resource);  Assert.assertEquals(1, CDOUtil.getViewSet(rset).getViews().length);  Assert.assertEquals(1, rset.getResources().size());  resource.delete(null);  assertTransient(resource); } 
