 public void decorate(Object element, IDecoration decoration){  if (element instanceof SybaseASABaseProxyTable)        {  SybaseASABaseProxyTable table = (SybaseASABaseProxyTable) element;  if(DSEUtil.checkIsShowOwner(element)){  String owner = table.getSchema().getOwner().getName();  StringBuffer suffix = new StringBuffer()                                         .append(" (")                                         .append(owner)                                         .append(")")                                         .append(" [")                                         .append(table.getRemoteObjectLocation())                                         .append("]");  decoration.addSuffix(suffix.toString());                             }  else{  decoration.addSuffix(" [" + table.getRemoteObjectLocation() + "]");                 }         }     } 
