 protected void fillContextMenu(IMenuManager manager, ITreeSelection selection){  final IBuddyCollaboration collaboration = (IBuddyCollaboration)selection.getFirstElement(); if (collaboration != null)       {  manager.add(new Separator());  for (IFacilityPaneCreator c : facilityPaneCreators.values())         {  String type = c.getType();  if (collaboration.getFacility(type) == null)           {  IAction action = new StartFacilityAction(collaboration, type, c.getImageDescriptor());  manager.add(action);           }         }  manager.add(new Separator());  manager.add(new SafeAction("Leave", "Leave this collaboration")         {  @Override  protected void safeRun() throws Exception           {  collaboration.leave();           }         });       }     } 
