 public EObject create(EClass eClass){  switch (eClass.getClassifierID())    {  case Model1Package.ADDRESS:  return (EObject)createAddress();  case Model1Package.COMPANY:  return (EObject)createCompany();  case Model1Package.SUPPLIER:  return (EObject)createSupplier();  case Model1Package.CUSTOMER:  return (EObject)createCustomer();  case Model1Package.ORDER:  return (EObject)createOrder();  case Model1Package.ORDER_DETAIL:  return (EObject)createOrderDetail();  case Model1Package.PURCHASE_ORDER:  return (EObject)createPurchaseOrder();  case Model1Package.SALES_ORDER:  return (EObject)createSalesOrder();  case Model1Package.CATEGORY:  return (EObject)createCategory();  case Model1Package.PRODUCT:  return (EObject)createProduct();  default:  throw new IllegalArgumentException("The class '" + eClass.getName() + "' is not valid classifier");     } } 
