 public QuickAccessElement[] getElements() {  if (cachedElements == null) {  List list = PlatformUI.getWorkbench().getPreferenceManager().getElements(PreferenceManager.PRE_ORDER);  Set uniqueElements = new HashSet(list);  IPreferenceNode[] preferences = (IPreferenceNode[]) uniqueElements.toArray(new IPreferenceNode[uniqueElements.size()]);  cachedElements = new QuickAccessElement[preferences.length];  for (int i = 0; i < preferences.length; i++) {  PreferenceElement preferenceElement = new PreferenceElement(  preferences[i], this);  cachedElements[i] = preferenceElement;  idToElement.put(preferenceElement.getId(), preferenceElement); 			} 		}  return cachedElements; 	}  public ImageDescriptor getImageDescriptor() {  return WorkbenchImages 				.getImageDescriptor(IWorkbenchGraphicConstants.IMG_OBJ_NODE); } 
