public class ConditionMiners { public static LicensingResult mine(LicensingConfiguration configuration, List<LicensingCondition> mined,  KeyKeeper keyKeeper, StreamCodec streamCodec, ConditionTransport transport, Iterable<Path> packs)   throws LicensingException {  String task = String.format("Mining licensing conditions for %s", configuration);  String source = ConditionMiners.class.getName();  List<LicensingResult> errors = new ArrayList<>();  for (Path path : packs) {  try (FileInputStream encoded = new FileInputStream(path.toFile());  ByteArrayOutputStream decoded = new ByteArrayOutputStream();  InputStream keyRing = keyKeeper.openKeyStream(configuration)) {  streamCodec.decodeStream(encoded, decoded, keyRing, null); } } } } 
