 public Object invokeOperation(String path, Object... parameters) throws Provider{  if (VABPathTools.isOperationInvokationPath(path)) { if(path.endsWith(OperationProvider.ASYNC)) {  path = removeSMElementPrefix(path);  path = path.replace(Operation.INVOKE + OperationProvider.ASYNC, "");  return submodelAPI.invokeAsync(path, parameters); 				} else {  path = removeSMElementPrefix(path); } } } 
