 @Override  public void initialize(Map values) {  super.initialize(values);  if (values.containsKey(MarkerSupportConstants.CONTAINS_KEY)) {   setContainsText((String) values  		.get(MarkerSupportConstants.CONTAINS_KEY));  setContainsModifier(MarkerSupportConstants.CONTAINS_KEY); 		} else if (values 				.containsKey(MarkerSupportConstants.DOES_NOT_CONTAIN_KEY)) {  setContainsText((String) values 					.get(MarkerSupportConstants.DOES_NOT_CONTAIN_KEY));  setContainsModifier(MarkerSupportConstants.DOES_NOT_CONTAIN_KEY); 		} 	} 
