public class PreviewWorkspaceViewProvider implements IViewProvider { private final Map<IPath, VView> trackedPaths = new LinkedHashMap<IPath, VView>();  /** 	 * Add new view model path to the list of available views in the preview. 	 * 	 * @param path The {@link IPath} to load 	 */  public void addViewModel(IPath path) {  trackedPaths.put(path, loadView(path)); 	}  private VView loadView(IPath path) { } } 
